// Get the Fecha field from Google Sheets
const fecha = $input.item.json.Fecha;

// Function to normalize date to dd/MM/yyyy format
function normalizeDate(dateStr) {
  if (!dateStr) return '';

  // Trim whitespace
  dateStr = dateStr.trim();

  // Split by /
  const parts = dateStr.split('/');

  if (parts.length !== 3) return dateStr; // Return original if not in expected format

  // Pad day and month with leading zeros if needed
  const day = parts[0].padStart(2, '0');
  const month = parts[1].padStart(2, '0');
  const year = parts[2];

  return `${day}/${month}/${year}`;
}

// Convert dd/MM/yyyy to Date object for comparison
function dateStringToDate(dateStr) {
  const parts = dateStr.split('/');
  if (parts.length !== 3) return null;
  // Month is 0-indexed in JavaScript Date
  return new Date(parts[2], parts[1] - 1, parts[0]);
}

// Normalize the date
const normalizedDate = normalizeDate(fecha);
const postDate = dateStringToDate(normalizedDate);
const today = new Date();
today.setHours(0, 0, 0, 0);

const isBeforeOrToday = postDate && postDate <= today;

// Return all original fields plus the normalized_date field and comparison result
return {
  json: {
    ...$input.item.json,
    normalized_date: normalizedDate,
    is_before_or_today: isBeforeOrToday
  }
};

// Extrae el texto del output de ChatGPT
const chatGPTText = $input.item.json.output[0].content[0].text;

// Limpia el texto por si tiene markdown
let cleanedText = chatGPTText.trim();

// Si el texto viene envuelto en ```json ... ```, quitarlo
if (cleanedText.startsWith('```json')) {
  cleanedText = cleanedText.replace(/^```json\s*/, '').replace(/```\s*$/, '');
} else if (cleanedText.startsWith('```')) {
  cleanedText = cleanedText.replace(/^```\s*/, '').replace(/```\s*$/, '');
}

// Convierte el string en JSON real
const sanityPost = JSON.parse(cleanedText);

// Retorna para el siguiente nodo (HTTP Request a Sanity)
return {
  json: sanityPost
};

